class Forecast{constructor(t){this.key="ZelFzLmE6NIMUDRt7A3OcLIy9t6kyHqT",this.weatherURI="http://dataservice.accuweather.com/currentconditions/v1/",this.cityURI="http://dataservice.accuweather.com/locations/v1/cities/search"}async updateCity(t){const e=await this.getCity(t);return{cityDetails:e,weather:await this.getWeather(e.Key)}}async getCity(t){const e=`?apikey=${this.key}&q=${t}`,a=await fetch(this.cityURI+e);return(await a.json())[0]}async getWeather(t){const e=`${t}?apikey=${this.key}`,a=await fetch(this.weatherURI+e);return(await a.json())[0]}}